
public class OpAritimeticos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n1,n2,n3;
		n1=10;
		n2=10;
		n3=n1+n2;
		System.out.println("o resutado da soma é: "+ n3);
		
		n1=30;
		n2=10;
		n3=n1-n2;
		System.out.println("o resutado da soma é: "+ n3);
		
		n1=10;
		n2=2;
		n3=n1*n2;
		System.out.println("o resutado da soma é: "+ n3);
		
		n1=40;
		n2=2;
		n3=n1/n2;
		System.out.println("o resutado da soma é: "+ n3);
	}

}
